import React, { useState, useEffect } from 'react';
import './App.css';

const questionsData = [
  {
    id: 1,
    question: "体の痛みはありますか？",
    options: [
      { text: "はい", nextQuestionId: 2 },
      { text: "いいえ", nextQuestionId: 3 }
    ]
  },
  {
    id: 2,
    question: "痛みがある部位を教えてください。",
    options: [
      { text: "頭", nextQuestionId: 4 },
      { text: "腹部", nextQuestionId: 4 }
    ]
  },
  {
    id: 3,
    question: "特に問題がないようですね。ほかに質問はありますか？",
    options: [
      { text: "はい", nextQuestionId: 5 },
      { text: "いいえ", nextQuestionId: null }
    ]
  },
  {
    id: 4,
    question: "痛みの度合いを教えてください（1〜10）。",
    options: Array.from({ length: 10 }, (_, i) => ({
      text: `${i + 1}`,
      nextQuestionId: null
    }))
  },
  {
    id: 5,
    question: "別の症状を教えてください。",
    options: [
      { text: "頭痛", nextQuestionId: 4 },
      { text: "発熱", nextQuestionId: 4 }
    ]
  }
];

function App() {
  const [currentQuestionId, setCurrentQuestionId] = useState(1);
  const currentQuestion = questionsData.find(q => q.id === currentQuestionId);
  const [recognitionActive, setRecognitionActive] = useState(false);
  const [transcript, setTranscript] = useState('');

    // 日本語の数字に対応するマッピング
    const numberMapping = {
      'いち': '1',
      'に': '2',
      'さん': '3',
      'よん': '4',
      'ご': '5',
      'ろく': '6',
      'なな': '7',
      'はち': '8',
      'きゅう': '9',
      'じゅう': '10'
    };

  // Web Speech API を使った音声認識
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.lang = 'ja-JP'; // 日本語を指定
      recognition.interimResults = false; // 結果を最終的なものにする
      recognition.continuous = false; // 1回だけ音声認識を行う
      recognition.onresult = (event) => {
        let speechResult = event.results[0][0].transcript.trim();
        console.log("音声認識結果:", speechResult);

        // 数字の発音を変換する（例: "いち" -> "1"）
        if (numberMapping[speechResult]) {
          speechResult = numberMapping[speechResult];
        }

        setTranscript(speechResult);
        handleVoiceCommand(speechResult); // 音声認識結果を元に回答を選択
      };

      recognition.onend = () => {
        setRecognitionActive(false);
      };

      // 音声認識を開始する関数
      const startRecognition = () => {
        if (!recognitionActive) {
          setRecognitionActive(true);
          recognition.start();
        }
      };

      document.querySelector('#start-recognition').addEventListener('click', startRecognition);

      return () => {
        document.querySelector('#start-recognition').removeEventListener('click', startRecognition);
      };
    } else {
      alert("Web Speech APIがこのブラウザでサポートされていません");
    }
  }, [recognitionActive]);

  // 音声認識の結果に基づいて質問に答える処理
  const handleVoiceCommand = (speechResult) => {
    const option = currentQuestion.options.find(o => o.text === speechResult.trim());
    if (option) {
      setCurrentQuestionId(option.nextQuestionId);
    } else {
      alert("認識できませんでした。もう一度試してください。");
    }
  };

  // ボタンのクリックで質問を進める処理
  const handleOptionClick = (nextQuestionId) => {
    if (nextQuestionId) {
      setCurrentQuestionId(nextQuestionId);
    } else {
      alert("問診が完了しました。");
    }
  };

  return (
    <div className="App">
      <h1>AI問診アプリ</h1>
      {currentQuestion && (
        <div className="container">
          <h2>{currentQuestion.question}</h2>
          <div className="button-container">
            {currentQuestion.options.map((option, index) => (
              <button key={index} onClick={() => handleOptionClick(option.nextQuestionId)}>
                {option.text}
              </button>
            ))}
          </div>
          <p>音声認識: {transcript}</p>
          <button id="start-recognition">
            音声で答える
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
